import React, { useState } from 'react';

function Login() {
  const [isExistingUser, setIsExistingUser] = useState(true);
  const [loginData, setLoginData] = useState({ username: '', password: '' });
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);

  // Handle input changes
  const handleLoginChange = (e) => {
    setLoginData({ ...loginData, [e.target.name]: e.target.value });
    setMessage('');
  };

  // Handle login form submission
  const handleLoginSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');
    try {
      const response = await fetch('http://localhost:5000/api/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(loginData),
      });
      const data = await response.json();
      if (response.ok) {
        setMessage('Login successful! Token: ' + data.access_token);
        // You can store the token in localStorage if needed
        // localStorage.setItem('access_token', data.access_token);
      } else {
        setMessage(data.error || 'Login failed.');
      }
    } catch (err) {
      setMessage('Network error. Is the backend running?');
    }
    setLoading(false);
  };

  return (
    <div style={{ maxWidth: 400, margin: '40px auto', padding: 24, border: '1px solid #ddd', borderRadius: 8 }}>
      <div style={{ display: 'flex', justifyContent: 'center', marginBottom: 24 }}>
        <button
          onClick={() => setIsExistingUser(true)}
          style={{
            padding: '10px 20px',
            marginRight: 8,
            background: isExistingUser ? '#007bff' : '#f0f0f0',
            color: isExistingUser ? '#fff' : '#333',
            border: 'none',
            borderRadius: 4,
            cursor: 'pointer'
          }}
        >
          Existing User
        </button>
        <button
          onClick={() => setIsExistingUser(false)}
          style={{
            padding: '10px 20px',
            background: !isExistingUser ? '#007bff' : '#f0f0f0',
            color: !isExistingUser ? '#fff' : '#333',
            border: 'none',
            borderRadius: 4,
            cursor: 'pointer'
          }}
          disabled
          title="Registration not implemented in this test"
        >
          New User
        </button>
      </div>

      {isExistingUser ? (
        <form onSubmit={handleLoginSubmit}>
          <h2>Login</h2>
          <div style={{ marginBottom: 16 }}>
            <label>Username</label>
            <input
              type="text"
              name="username"
              value={loginData.username}
              onChange={handleLoginChange}
              required
              style={{ width: '100%', padding: 8, marginTop: 4 }}
            />
          </div>
          <div style={{ marginBottom: 16 }}>
            <label>Password</label>
            <input
              type="password"
              name="password"
              value={loginData.password}
              onChange={handleLoginChange}
              required
              style={{ width: '100%', padding: 8, marginTop: 4 }}
            />
          </div>
          <button
            type="submit"
            style={{
              width: '100%',
              padding: 10,
              background: '#007bff',
              color: '#fff',
              border: 'none',
              borderRadius: 4
            }}
            disabled={loading}
          >
            {loading ? 'Logging in...' : 'Login'}
          </button>
          <div style={{ marginTop: 12, textAlign: 'right' }}>
            <a href="#" style={{ color: '#007bff', textDecoration: 'underline' }}>
              Forgot password?
            </a>
          </div>
          {message && (
            <div style={{ marginTop: 16, color: message.startsWith('Login successful') ? 'green' : 'red' }}>
              {message}
            </div>
          )}
        </form>
      ) : (
        <div>
          <h2>Registration is not implemented in this test.</h2>
        </div>
      )}
    </div>
  );
}

export default Login;
