import React, { useState } from 'react';
import { useSearchParams, useNavigate } from 'react-router-dom';

function ResetPassword() {
  const [searchParams] = useSearchParams();
  const username = searchParams.get('username') || '';
  const [otp, setOtp] = useState('');
  const [password, setPassword] = useState('');
  const [msg, setMsg] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMsg('');
    try {
      const response = await fetch('/api/reset-password', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, otp, password })
      });
      const data = await response.json();
      if (response.ok) {
        setMsg(data.message);
        setTimeout(() => {
          navigate('/'); // Redirect to login after success
        }, 1500);
      } else {
        setMsg(data.error || 'Something went wrong.');
      }
    } catch {
      setMsg('Network error.');
    }
    setLoading(false);
  };

  return (
    <form onSubmit={handleSubmit} style={{ maxWidth: 400, margin: '40px auto', padding: 24, border: '1px solid #ddd', borderRadius: 8 }}>
      <h2>Reset Password</h2>
      <input
        value={username}
        disabled
        style={{ width: '100%', padding: 8, marginBottom: 16, background: '#f0f0f0' }}
      />
      <input
        value={otp}
        onChange={e => setOtp(e.target.value)}
        placeholder="OTP"
        required
        style={{ width: '100%', padding: 8, marginBottom: 16 }}
      />
      <input
        type="password"
        value={password}
        onChange={e => setPassword(e.target.value)}
        placeholder="New Password"
        required
        style={{ width: '100%', padding: 8, marginBottom: 16 }}
      />
      <button type="submit" disabled={loading} style={{ width: '100%', padding: 10 }}>
        {loading ? 'Resetting...' : 'Reset Password'}
      </button>
      {msg && <div style={{ marginTop: 16 }}>{msg}</div>}
    </form>
  );
}

export default ResetPassword;
