import React, { useState } from 'react';

function Login() {
  const [isExistingUser, setIsExistingUser] = useState(true);
  const [showForgot, setShowForgot] = useState(false);
  const [showReset, setShowReset] = useState(false);

  // ... (existing states for login and registration)

  // Forgot password state
  const [forgotEmail, setForgotEmail] = useState('');
  const [forgotMsg, setForgotMsg] = useState('');
  const [forgotLoading, setForgotLoading] = useState(false);

  // Reset password state
  const [resetToken, setResetToken] = useState('');
  const [resetPassword, setResetPassword] = useState('');
  const [resetMsg, setResetMsg] = useState('');
  const [resetLoading, setResetLoading] = useState(false);

  // ... (existing handlers)

  // Handle forgot password submit
  const handleForgotSubmit = async (e) => {
    e.preventDefault();
    setForgotMsg('');
    setForgotLoading(true);
    try {
      const response = await fetch('/api/forgot-password', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email: forgotEmail })
      });
      const data = await response.json();
      setForgotMsg(data.message);
    } catch (err) {
      setForgotMsg('Network error.');
    }
    setForgotLoading(false);
  };

  // Handle reset password submit
  const handleResetSubmit = async (e) => {
    e.preventDefault();
    setResetMsg('');
    setResetLoading(true);
    try {
      const response = await fetch('/api/reset-password', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ token: resetToken, password: resetPassword })
      });
      const data = await response.json();
      if (response.ok) setResetMsg('Password reset successful! You can now log in.');
      else setResetMsg(data.error || 'Reset failed.');
    } catch (err) {
      setResetMsg('Network error.');
    }
    setResetLoading(false);
  };

  // ... (existing UI)

  return (
    <div style={{ maxWidth: 400, margin: '40px auto', padding: 24, border: '1px solid #ddd', borderRadius: 8 }}>
      {/* Toggle between login, register, forgot, reset */}
      {!showForgot && !showReset && (
        <div style={{ display: 'flex', justifyContent: 'center', marginBottom: 24 }}>
          <button
            onClick={() => setIsExistingUser(true)}
            style={{
              padding: '10px 20px',
              marginRight: 8,
              background: isExistingUser ? '#007bff' : '#f0f0f0',
              color: isExistingUser ? '#fff' : '#333',
              border: 'none',
              borderRadius: 4,
              cursor: 'pointer'
            }}
          >
            Existing User
          </button>
          <button
            onClick={() => setIsExistingUser(false)}
            style={{
              padding: '10px 20px',
              background: !isExistingUser ? '#007bff' : '#f0f0f0',
              color: !isExistingUser ? '#fff' : '#333',
              border: 'none',
              borderRadius: 4,
              cursor: 'pointer'
            }}
          >
            New User
          </button>
        </div>
      )}

      {/* Login Form */}
      {isExistingUser && !showForgot && !showReset && (
        <form onSubmit={handleLoginSubmit}>
          <h2>Login</h2>
          {/* ... username & password fields ... */}
          <div style={{ marginBottom: 16 }}>
            <label>Username</label>
            <input
              type="text"
              name="username"
              value={loginData.username}
              onChange={handleLoginChange}
              required
              style={{ width: '100%', padding: 8, marginTop: 4 }}
            />
          </div>
          <div style={{ marginBottom: 16 }}>
            <label>Password</label>
            <input
              type="password"
              name="password"
              value={loginData.password}
              onChange={handleLoginChange}
              required
              style={{ width: '100%', padding: 8, marginTop: 4 }}
            />
          </div>
          <button
            type="submit"
            style={{
              width: '100%',
              padding: 10,
              background: '#007bff',
              color: '#fff',
              border: 'none',
              borderRadius: 4
            }}
            disabled={loginLoading}
          >
            {loginLoading ? 'Logging in...' : 'Login'}
          </button>
          <div style={{ marginTop: 12, textAlign: 'right' }}>
            <a href="#" onClick={() => setShowForgot(true)} style={{ color: '#007bff', textDecoration: 'underline' }}>
              Forgot password?
            </a>
          </div>
          {loginMsg && (
            <div style={{ marginTop: 16, color: loginMsg.startsWith('Login successful') ? 'green' : 'red' }}>
              {loginMsg}
            </div>
          )}
        </form>
      )}

      {/* Registration Form */}
      {!isExistingUser && !showForgot && !showReset && (
        <form onSubmit={handleRegSubmit}>
          <h2>Register</h2>
          {/* ... registration fields ... */}
          <div style={{ marginBottom: 16 }}>
            <label>Username</label>
            <input
              type="text"
              name="username"
              value={regData.username}
              onChange={handleRegChange}
              required
              style={{ width: '100%', padding: 8, marginTop: 4 }}
            />
          </div>
          <div style={{ marginBottom: 16 }}>
            <label>Email</label>
            <input
              type="email"
              name="email"
              value={regData.email}
              onChange={handleRegChange}
              required
              style={{ width: '100%', padding: 8, marginTop: 4 }}
            />
          </div>
          <div style={{ marginBottom: 16 }}>
            <label>Password</label>
            <input
              type="password"
              name="password"
              value={regData.password}
              onChange={handleRegChange}
              required
              style={{ width: '100%', padding: 8, marginTop: 4 }}
            />
          </div>
          <div style={{ marginBottom: 16 }}>
            <label>Confirm Password</label>
            <input
              type="password"
              name="confirmPassword"
              value={regData.confirmPassword}
              onChange={handleRegChange}
              required
              style={{ width: '100%', padding: 8, marginTop: 4 }}
            />
          </div>
          <button
            type="submit"
            style={{
              width: '100%',
              padding: 10,
              background: '#007bff',
              color: '#fff',
              border: 'none',
              borderRadius: 4
            }}
            disabled={regLoading}
          >
            {regLoading ? 'Registering...' : 'Register'}
          </button>
          {regMsg && (
            <div style={{ marginTop: 16, color: regMsg.startsWith('Registration successful') ? 'green' : 'red' }}>
              {regMsg}
            </div>
          )}
        </form>
      )}

      {/* Forgot Password Form */}
      {showForgot && !showReset && (
        <form onSubmit={handleForgotSubmit}>
          <h2>Forgot Password</h2>
          <div style={{ marginBottom: 16 }}>
            <label>Email</label>
            <input
              type="email"
              value={forgotEmail}
              onChange={e => setForgotEmail(e.target.value)}
              required
              style={{ width: '100%', padding: 8, marginTop: 4 }}
            />
          </div>
          <button
            type="submit"
            style={{
              width: '100%',
              padding: 10,
              background: '#007bff',
              color: '#fff',
              border: 'none',
              borderRadius: 4
            }}
            disabled={forgotLoading}
          >
            {forgotLoading ? 'Sending...' : 'Send Reset Link'}
          </button>
          <div style={{ marginTop: 12 }}>
            <a href="#" onClick={() => setShowForgot(false)} style={{ color: '#007bff', textDecoration: 'underline' }}>
              Back to Login
            </a>
          </div>
          {forgotMsg && (
            <div style={{ marginTop: 16, color: 'green' }}>
              {forgotMsg}
            </div>
          )}
        </form>
      )}

      {/* Reset Password Form (for demonstration, token is entered manually) */}
      {showReset && (
        <form onSubmit={handleResetSubmit}>
          <h2>Reset Password</h2>
          <div style={{ marginBottom: 16 }}>
            <label>Reset Token</label>
            <input
              type="text"
              value={resetToken}
              onChange={e => setResetToken(e.target.value)}
              required
              style={{ width: '100%', padding: 8, marginTop: 4 }}
            />
          </div>
          <div style={{ marginBottom: 16 }}>
            <label>New Password</label>
            <input
              type="password"
              value={resetPassword}
              onChange={e => setResetPassword(e.target.value)}
              required
              style={{ width: '100%', padding: 8, marginTop: 4 }}
            />
          </div>
          <button
            type="submit"
            style={{
              width: '100%',
              padding: 10,
              background: '#007bff',
              color: '#fff',
              border: 'none',
              borderRadius: 4
            }}
            disabled={resetLoading}
          >
            {resetLoading ? 'Resetting...' : 'Reset Password'}
          </button>
          <div style={{ marginTop: 12 }}>
            <a href="#" onClick={() => setShowReset(false)} style={{ color: '#007bff', textDecoration: 'underline' }}>
              Back to Login
            </a>
          </div>
          {resetMsg && (
            <div style={{ marginTop: 16, color: resetMsg.startsWith('Password reset successful') ? 'green' : 'red' }}>
              {resetMsg}
            </div>
          )}
        </form>
      )}

      {/* Option to open reset form manually for demo/testing */}
      {showForgot && (
        <div style={{ marginTop: 16, textAlign: 'center' }}>
          <a href="#" onClick={() => setShowReset(true)} style={{ color: '#007bff', textDecoration: 'underline' }}>
            Already have a reset token? Reset Password
          </a>
        </div>
      )}
    </div>
  );
}

export default Login;
