import React, { useState } from 'react';

function ForgotPassword() {
  const [msg, setMsg] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSend = async () => {
    setLoading(true);
    setMsg('');
    try {
      const token = localStorage.getItem('access_token'); // JWT from login
      const response = await fetch('/api/forgot-password', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
      });
      const data = await response.json();
      setMsg(data.message);
    } catch {
      setMsg('Network error. Is the backend running?');
    }
    setLoading(false);
  };

  return (
    <div style={{ maxWidth: 400, margin: '40px auto', padding: 24, border: '1px solid #ddd', borderRadius: 8 }}>
      <h2>Forgot Password</h2>
      <p>Click the button below to receive a reset link at your registered email.</p>
      <button onClick={handleSend} disabled={loading} style={{ width: '100%', padding: 10 }}>
        {loading ? 'Sending...' : 'Send Reset Link'}
      </button>
      {msg && <div style={{ marginTop: 16 }}>{msg}</div>}
    </div>
  );
}

export default ForgotPassword;
