import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function ForgotPassword() {
  const [username, setUsername] = useState('');
  const [msg, setMsg] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMsg('');
    try {
      const response = await fetch('/api/forgot-password', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username })
      });
      const data = await response.json();
      if (response.ok) {
        setMsg(data.message);
        // Redirect to reset password page, passing username as query param
        setTimeout(() => {
          navigate(`/reset-password?username=${encodeURIComponent(username)}`);
        }, 1200); // Let user see the message for 1.2s before redirect
      } else {
        setMsg(data.error || 'Something went wrong.');
      }
    } catch {
      setMsg('Network error.');
    }
    setLoading(false);
  };

  return (
    <form onSubmit={handleSubmit} style={{ maxWidth: 400, margin: '40px auto', padding: 24, border: '1px solid #ddd', borderRadius: 8 }}>
      <h2>Forgot Password</h2>
      <input
        value={username}
        onChange={e => setUsername(e.target.value)}
        placeholder="Username"
        required
        style={{ width: '100%', padding: 8, marginBottom: 16 }}
      />
      <button type="submit" disabled={loading} style={{ width: '100%', padding: 10 }}>
        {loading ? 'Sending...' : 'Send OTP'}
      </button>
      {msg && <div style={{ marginTop: 16 }}>{msg}</div>}
    </form>
  );
}

export default ForgotPassword;
